import React, {Component} from 'react';
import {View, Text} from "react-native";

export class DetailsScreen extends Component {

    getCountryData = () => {
        fetch(`https://restcountries.eu/rest/v2/name/${this.state.input}`)
        .then(response => response.json())
        .then(detalleJson => this.setState({detalle: detalleJson[0]}))
        }
        componentDidMount(){
          this.getCountryData();
      }

    handlerTextInput(valText){
        this.setState({text: valText});
    }

    render(){
        return (
            <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                <Text>Valor: {this.props.route.params.text}</Text>
            </View>
        );
    }

  }
